import React, { useState } from "react";
import axios from "axios";
import { calculateETC } from "./utils/calculateETC";

const BASE_URL = process.env.REACT_APP_API_URL ?? "http://localhost:3000";

const AddOrder = () => {
    const [clientName, setClientName] = useState("");
    const [clientContact, setClientContact] = useState("");
    const [category, setCategory] = useState("New Mix");
    const [paintType, setPaintType] = useState("");
    const [colorCode, setColorCode] = useState("");
    const [loading, setLoading] = useState(false);
    const [message, setMessage] = useState("");

    // ‚úÖ Generate Unique Transaction ID (Using Timestamp & Random Digits)
    const generateTransactionID = () => {
        const datePart = new Date().toISOString().slice(0, 10).replace(/-/g, "");
        const randomPart = Math.floor(1000 + Math.random() * 9000); // 4-digit random number
        return `TXN-${datePart}-${randomPart}`;
    };

    // ‚úÖ Validate Contact Number (Only 10 digits)
    const validateContact = (input) => {
        return /^\d{10}$/.test(input);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setMessage("");

        console.log("üìù Validating form input...");

        if (!validateContact(clientContact)) {
            alert("‚ùå Contact number must be exactly 10 digits!");
            setLoading(false);
            return;
        }

        if (paintType.trim() === "") {
            alert("‚ùå Paint Type cannot be empty!");
            setLoading(false);
            return;
        }

        const newTransactionID = generateTransactionID();
        console.log("‚úÖ Generated Unique Transaction ID:", newTransactionID);

        try {
            console.log("‚è∞ Waking backend...");
            await axios.get(`${BASE_URL}/api/status`); // ‚úÖ Updated wake-up request

            console.log("üîç Checking for duplicate orders...");
            const existingOrderCheck = await axios.get(`${BASE_URL}/api/check-duplicate`, {
                params: { client_name: clientName, client_contact: clientContact, paint_type: paintType, category },
                timeout: 20000
            });

            if (existingOrderCheck.data.exists) {
                alert("‚ùå This order already exists! Duplicate entries are not allowed.");
                setLoading(false);
                return;
            }
        } catch (error) {
            console.warn("‚ö†Ô∏è Skipping duplicate check: Endpoint not found");
        }

        const newOrder = {
            transaction_id: newTransactionID,
            client_name: clientName,
            client_contact: clientContact,
            paint_type: paintType,
            color_code: category === "New Mix" ? "Pending" : colorCode || "N/A",
            category,
            start_time: new Date().toISOString(),
            estimated_completion: calculateETC(category, 5) || "N/A",
            current_status: "Waiting"
        };

        console.log("üöÄ Sending order data:", newOrder);

        try {
            const response = await axios.post(`${BASE_URL}/api/orders`, newOrder, {
                headers: { "Content-Type": "application/json", Accept: "application/json" }
            });

            if (response.data?.success && response.data?.data) {
                console.log("‚úÖ Order added successfully:", response.data.data);
                setMessage("‚úÖ Order added successfully!");
            } else {
                console.error("üö® Error: Order data missing in response!", response.data);
                setMessage("üö® Error adding order!");
            }

        } catch (error) {
            if (error.response?.status === 400) {
                console.error("üö® Bad request error:", error.response.data);
                setMessage("‚ùå Error: Invalid data format!");
            } else if (error.response?.status === 500) {
                console.error("üö® Internal server error:", error.response.data);
                setMessage("‚ùå Error: Backend issue detected!");
            } else {
                console.error("üö® Unexpected error:", error.message);
                setMessage("‚ùå Error adding order: " + error.message);
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="container mt-4">
            <h2>Add New Order</h2>
            <form onSubmit={handleSubmit}>
                <label>Client Name:</label>
                <input type="text" className="form-control" value={clientName} onChange={(e) => setClientName(e.target.value)} required />

                <label>Client Contact:</label>
                <input type="text" className="form-control" value={clientContact} onChange={(e) => setClientContact(e.target.value)} required />

                <label>Category:</label>
                <select className="form-control" value={category} onChange={(e) => setCategory(e.target.value)}>
                    <option>New Mix</option>
                    <option>Reorder Mix</option>
                    <option>Colour Code</option>
                </select>

                <label>Paint Type:</label>
                <input type="text" className="form-control" value={paintType} onChange={(e) => setPaintType(e.target.value)} required />

                <label>Colour Code:</label>
                <input type="text" className="form-control" value={colorCode} onChange={(e) => setColorCode(e.target.value)} disabled={category === "New Mix"} />

                <button type="submit" className="btn btn-primary mt-3" disabled={loading}>
                    {loading ? "Submitting..." : "Add Order"}
                </button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
};

export default AddOrder;
